import React,{useEffect,useState} from 'react';
import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'

function App() {
   const[latest,setLatest]=useState([]);
   const[results,setResults]=useState([]);
     useEffect(()=>{
         axios
         .all([
         axios.get("https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief"),
         axios.get("https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/latest")
         ])
         .then(responseArr=>{
             setLatest(responseArr[0].data);
             setResults(responseArr[1].data);
         })
         .catch(err=>{
             console.log(err);
         });
     },[]);

  const countries=results.map(data=>{
      return (
          <Card 
          bg="light"
          text="dark"
          className="text-center"
          style={{margin:"10px"}}
          >
          <Card.Body>
          <Card.Title>{data.countryregion}</Card.Title>
          <Card.Text>Confirmed{data.confirmed}</Card.Text>
          </Card.Body>
          
          </Card>
      );
  })




    return <div>
    <CardDeck>
  <Card  bg="secondary" text="white" className="text-center"  style={{margin:"10px"}}>
  
    <Card.Body>
      <Card.Title>Confirmed</Card.Title>
      <Card.Text>
      {latest.confirmed}
      </Card.Text>
    </Card.Body>
   
  </Card>
  <Card bg="danger" text="white" className="text-center"  style={{margin:"10px"}}>
   
    <Card.Body>
      <Card.Title>Deaths</Card.Title>
      <Card.Text>
      {latest.deaths}
      </Card.Text>
    </Card.Body>
    
  </Card>
  <Card 
  bg="success" 
  text="white" 
  className="text-center"
  style={{margin:"10px"}}
  >
   
    <Card.Body>
      <Card.Title>Recovered</Card.Title>
      <Card.Text>
      {latest.recovered}
      </Card.Text>
    </Card.Body>
   
  </Card>
</CardDeck>
{countries}
    </div>

    
}


export default App;
