import React,{useEffect,useState} from 'react';
import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'
import "bootstrap/dist/css/bootstrap.min.css"
import axios from 'axios'
import CardColumns from "react-bootstrap/CardColumns"
function App() {
   const[latest,setLatest]=useState([]);
   const[results,setResults]=useState([]);
     useEffect(()=>{
         axios
         .all([
         axios.get("https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/brief"),
         axios.get("https://wuhan-coronavirus-api.laeyoung.endpoint.ainize.ai/jhu-edu/latest")
         ])
         .then(responseArr=>{
             setLatest(responseArr[0].data);
             setResults(responseArr[1].data);
         })
         .catch(err=>{
             console.log(err);
         });
     },[]);

  const countries=results.map((data,i)=>{
      return (
          <Card 
          key={i}
          bg="light"
          text="dark"
          className="text-center"
          style={{margin:"10px"}}
          >
          <Card.Body>
          <Card.Title>Country {data.countryregion}</Card.Title>
          <Card.Title>Province State {data.provincestate}</Card.Title>
          <Card.Text>Confirmed {data.confirmed}</Card.Text>
          <Card.Text>Deaths{data.deaths}</Card.Text>
          <Card.Text>Recovered{data.recovered}</Card.Text>
         
          </Card.Body>
          
          </Card>
      );
  })




    return <div>
    <CardDeck>
  <Card  bg="secondary" text="white" className="text-center"  style={{margin:"10px"}}>
  
    <Card.Body>
      <Card.Title>Confirmed</Card.Title>
      <Card.Text>
      {latest.confirmed}
      </Card.Text>
    </Card.Body>
   
  </Card>
  <Card bg="danger" text="white" className="text-center"  style={{margin:"10px"}}>
   
    <Card.Body>
      <Card.Title>Deaths</Card.Title>
      <Card.Text>
      {latest.deaths}
      </Card.Text>
    </Card.Body>
    
  </Card>
  <Card 
  bg="success" 
  text="white" 
  className="text-center"
  style={{margin:"10px"}}
  >
   
    <Card.Body>
      <Card.Title>Recovered</Card.Title>
      <Card.Text>
      {latest.recovered}
      </Card.Text>
    </Card.Body>
   
  </Card>
</CardDeck>
<CardColumns>{countries}</CardColumns>

    </div>

    
}


export default App;
